class Solution:
    def threeSum(self, nums):
        """
        :type nums: List[int]
        :rtype: List[List[int]]
        """
        ans = []
        nums.sort()
        nums_length = len(nums)
        prev = float("inf")
        if nums_length < 3:
            return ans
        for index in range(0, nums_length-2):
            l = index + 1
            r = nums_length-1
            if nums[index] == prev:
                continue
            prev_l = float("inf")
            while l<r:
                # print(l, prev_l)
                sum3 = nums[index] + nums[l] + nums[r]
                if sum3 == 0:
                    ans.append([nums[index], nums[l], nums[r]])
                    prev_l = nums[l]
                    l+=1
                    r-=1
                elif sum3 > 0:
                    r-=1
                elif sum3 < 0:
                    prev_l = nums[l]
                    l+=1
                while l<r and nums[l] == prev_l:
                    l+=1
            prev = nums[index]
        return ans



